declare module 'arui-feather/footer' {
    import { Component, ReactNode } from 'react';

    export interface FooterProps {

        /**
         * Меню в подвале
         */
        menu?: ReactNode;
        /**
         * Дополнительный текст
         */
        additional?: ReactNode;
        /**
         * Содержимое блока соц. сетей
         */
        social?: ReactNode;
        /**
         * Отображение блока соц. сетей
         */
        showSocial?: boolean;
        /**
         * Содержимое блока копирайта
         */
        copyright?: ReactNode;
        /**
         * Отображение годов в копирайте
         */
        showYears?: boolean;
        /**
         * Тема компонента
         */
        theme?: 'alfa-on-color' | 'alfa-on-white';
        /**
         * Дополнительный класс
         */
        className?: Function | string;
    }


    /**
     * Компонент подвала сайта.
Обычно используется совместно с компонентом `Page`.

@example
```javascript
import Page from 'arui-feather/page';
import Header from 'arui-feather/header';
import Footer from 'arui-feather/footer';

<Page header={ <Header /> } footer={ <Footer /> }>
    Контент страницы...
</Page>
```
     */
    export default class Footer extends Component<FooterProps, any> {

    }
}