declare module 'arui-feather/form' {
    import { Component, ReactNode } from 'react';

    export interface FormProps {

        /**
         * Способ кодирования данных формы при их отправке
         */
        enctype?: 'application/x-www-form-urlencoded' | 'multipart/form-data' | 'text/plain';
        /**
         * Адрес отправки данных на сервер
         */
        action?: string;
        /**
         * Метод запроса
         */
        method?: 'post' | 'get';
        /**
         * Тип формы
         */
        view?: 'line' | 'normal';
        /**
         * Размер компонента
         */
        size?: 's' | 'm' | 'l' | 'xl';
        /**
         * Футер для формы
         */
        footer?: ReactNode;
        /**
         * Управление встроенным в браузер механизмом валидации формы
         */
        noValidate?: boolean;
        /**
         * Управление автозаполнением формы
         */
        autocomplete?: boolean;
        /**
         * Дочерние элементы формы
         */
        children?: Array<ReactNode> | ReactNode;
        /**
         * Тема компонента
         */
        theme?: 'alfa-on-color' | 'alfa-on-white';
        /**
         * Дополнительный класс
         */
        className?: Function | string;
        /**
         * Идентификатор компонента в DOM
         */
        id?: string;
        /**
         * Имя компонента в DOM
         */
        name?: string;
        /**
         * Обработчик отправки формы
         */
        onSubmit?: Function;
    }


    /**
     * Компонент формы.
     */
    export default class Form extends Component<FormProps, any> {

    }
}